import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import axios from '../axios';
import './css/ComposeMessage.css';

export default function ComposeMessage() {
    const [recipientUsername, setRecipientUsername] = useState('');
    const [subject, setSubject] = useState('');
    const [content, setContent] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const username = recipientUsername.replace('@', ''); // Remove @ if present
            console.log('Fetching user:', username);
            const recipientResponse = await axios.get(`/api/users/by-username/${username}`);
            console.log('Recipient response:', recipientResponse.data);
            const recipientId = recipientResponse.data._id;
            console.log('Sending message to:', recipientId);
            const messageResponse = await axios.post('/api/messages', { recipientId, subject, content });
            console.log('Message sent:', messageResponse.data);
            alert('Message sent successfully!');
            setRecipientUsername('');
            setSubject('');
            setContent('');
        } catch (error) {
            console.error('Error sending message:', error.response ? error.response.data : error.message);
            if (error.response && error.response.status === 404) {
                alert('User not found. Please check the username and try again.');
            } else {
                alert('Failed to send message. Please try again.');
            }
        }
    };

    return (
        <div className="compose-message">
            <h3>Compose New Message</h3>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Recipient Username"
                    value={recipientUsername}
                    onChange={(e) => setRecipientUsername(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Subject"
                    value={subject}
                    onChange={(e) => setSubject(e.target.value)}
                    required
                />
                <ReactQuill
                    theme="snow"
                    value={content}
                    onChange={setContent}
                    placeholder="Write your message here..."
                />
                <button type="submit">Send Message</button>
            </form>
        </div>
    );
}
