import React, { useState, useEffect } from 'react';
import axios from '../axios';
import ThreadList from './ThreadList';
import CreateThread from './CreateThread';
import './css/ForumPage.css';

const ForumPage = () => {
    const [threads, setThreads] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchThreads();
    }, []);

    const fetchThreads = async () => {
        setLoading(true);
        try {
            const response = await axios.get('/api/threads');
            setThreads(response.data);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching threads:', error);
            setError('Failed to fetch threads. Please try again later.');
            setLoading(false);
        }
    };

    const handleThreadCreated = (newThread) => {
        setThreads(prevThreads => [newThread, ...prevThreads]);
    };

    if (loading) return <div>Loading forum...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="forum-page">
            <h1>Forum Threads</h1>
            <CreateThread onThreadCreated={handleThreadCreated} />
            {threads.length === 0 ? (
                <p>No threads found. Be the first to create a thread!</p>
            ) : (
                <ThreadList threads={threads} />
            )}
        </div>
    );
};

export default ForumPage;
