import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from '../axios';
import './css/MessagesList.css';

const MessagesList = () => {
    const [conversations, setConversations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchConversations = async () => {
            try {
                const response = await axios.get('/api/messages');
                setConversations(response.data);
                setLoading(false);
            } catch (err) {
                console.error('Error fetching conversations:', err);
                setError('Failed to load conversations. Please try again.');
                setLoading(false);
            }
        };

        fetchConversations();
    }, []);

    if (loading) return <div className="loading">Loading conversations...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="messages-list">
            <h2>Your Conversations</h2>
            {conversations.length === 0 ? (
                <p>You have no conversations yet.</p>
            ) : (
                <ul>
                    {conversations.map((conversation) => (
                        <li key={conversation._id}>
                            <Link to={`/messages/${conversation._id}`}>
                                <div className="conversation-preview">
                                    <img src={conversation.otherUser.profilePicture || '/default-avatar.png'} alt={conversation.otherUser.username} className="avatar" />
                                    <div className="conversation-info">
                                        <h3>{conversation.otherUser.username}</h3>
                                        <p>{conversation.lastMessage.content}</p>
                                    </div>
                                    <span className="timestamp">{new Date(conversation.lastMessage.timestamp).toLocaleString()}</span>
                                </div>
                            </Link>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default MessagesList;
