import React, { useState, useEffect } from 'react';
import axios from '../axios';
import './css/Sent.css';

export default function Sent() {
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        const fetchSentMessages = async () => {
            try {
                const response = await axios.get('/api/messages/sent');
                setMessages(response.data);
            } catch (error) {
                console.error('Error fetching sent messages:', error);
            }
        };

        fetchSentMessages();
    }, []);

    return (
        <div className="sent">
            <h3>Sent Messages</h3>
            <ul className="message-list">
                {messages.map(message => (
                    <li key={message._id} className="message-item">
                        <div className="message-header">
                            <span className="recipient">To: {message.recipient.username}</span>
                            <span className="date">{new Date(message.createdAt).toLocaleString()}</span>
                        </div>
                        <div className="message-subject">{message.subject}</div>
                        <div className="message-preview">{message.content.substring(0, 100)}...</div>
                    </li>
                ))}
            </ul>
        </div>
    );
}
