import React from 'react';
import './css/Post.css';
import { Heart, MessageCircle, Share2, MoreHorizontal } from 'lucide-react';

export default function Post({ post }) {
    const defaultAvatar = "https://via.placeholder.com/40"; // Placeholder image URL

    return (
        <div className="post">
            <div className="post-header">
                <img 
                    src={post.user.profilePicture || defaultAvatar} 
                    alt={post.user} 
                    className="avatar" 
                    onError={(e) => { e.target.onerror = null; e.target.src = defaultAvatar; }}
                />
                <div className="post-info">
                    <h3>{post.user}</h3>
                    <span>{new Date(post.createdAt).toLocaleString()}</span>
                </div>
                <MoreHorizontal size={20} className="more-options" />
            </div>
            <div className="post-content">
                {post.title && <h4>{post.title}</h4>}
                <p>{post.content}</p>
            </div>
            <div className="post-actions">
                <button><Heart size={20} /> 15</button>
                <button><MessageCircle size={20} /> 3</button>
                <button><Share2 size={20} /> 2</button>
            </div>
            <div className="post-comment">
                <input type="text" placeholder="Write a comment..." />
                <button>Send</button>
            </div>
        </div>
    );
}
