import React, { useState, useEffect } from 'react';
import './css/Post.css';
import { Heart, MessageCircle, Share2, MoreHorizontal, Send } from 'lucide-react';
import axios from '../axios';

export default function Post({ post }) {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const defaultAvatar = "https://via.placeholder.com/40";

    useEffect(() => {
        fetchComments();
    }, [post._id]);

    const fetchComments = async () => {
        try {
            const response = await axios.get(`/api/posts/${post._id}/comments`);
            setComments(response.data);
        } catch (error) {
            console.error('Error fetching comments:', error);
        }
    };

    const handleAddComment = async () => {
        if (!newComment.trim()) return;

        try {
            const user = JSON.parse(localStorage.getItem('user'));
            const response = await axios.post(`/api/posts/${post._id}/comments`, {
                content: newComment,
                userId: user._id,
                username: user.username
            });
            setComments([...comments, response.data]);
            setNewComment('');
        } catch (error) {
            console.error('Error adding comment:', error);
        }
    };

    return (
        <div className="post">
            <div className="post-header">
                <img 
                    src={post.user.profilePicture || defaultAvatar} 
                    alt={post.user} 
                    className="avatar" 
                    onError={(e) => { e.target.onerror = null; e.target.src = defaultAvatar; }}
                />
                <div className="post-info">
                    <h3>{post.user}</h3>
                    <span>{new Date(post.createdAt).toLocaleString()}</span>
                </div>
                <MoreHorizontal size={20} className="more-options" />
            </div>
            <div className="post-content">
                {post.title && <h4>{post.title}</h4>}
                <p>{post.content}</p>
            </div>
            <div className="post-actions">
                <button><Heart size={20} /> {post.likes.length}</button>
                <button><MessageCircle size={20} /> {comments.length}</button>
                <button><Share2 size={20} /></button>
            </div>
            <div className="post-comments">
                {comments.map((comment) => (
                    <div key={comment._id} className="comment">
                        <img 
                            src={comment.user.profilePicture || defaultAvatar} 
                            alt={comment.username} 
                            className="avatar-small" 
                        />
                        <div className="comment-content">
                            <strong>{comment.username}</strong>
                            <p>{comment.content}</p>
                        </div>
                    </div>
                ))}
            </div>
            <div className="post-comment-input">
                <input
                    type="text"
                    placeholder="Write a comment..."
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                />
                <button onClick={handleAddComment}><Send size={20} /></button>
            </div>
        </div>
    );
}
