import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Changed from useHistory to useNavigate
import axios from '../axios';
import './css/Profile.css';

const Profile = () => {
    const navigate = useNavigate(); // Changed from useHistory()
    const [user, setUser] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState({});
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [activeTab, setActiveTab] = useState('profile');

    useEffect(() => {
        const fetchUserProfile = async () => {
            try {
                const response = await axios.get('/api/users/profile');
                setUser(response.data);
                setFormData(response.data);
            } catch (error) {
                console.error('Error fetching user profile:', error);
                if (error.response && error.response.status === 401) {
                    // Token is invalid or expired
                    localStorage.removeItem('token');
                    localStorage.removeItem('user');
                    navigate('/login');
                } else {
                    setError('Failed to load profile. Please try again.');
                }
            }
        };

        fetchUserProfile();
    }, [navigate]); // Added navigate to the dependency array

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');
        try {
            const response = await axios.put('/api/users/profile', formData);
            setUser(response.data);
            setIsEditing(false);
            setSuccess('Profile updated successfully!');
        } catch (error) {
            console.error('Error updating profile:', error);
            setError('Failed to update profile. Please try again.');
        }
    };

    const handlePasswordChange = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');
        if (formData.newPassword !== formData.confirmPassword) {
            setError('New passwords do not match.');
            return;
        }
        try {
            await axios.put('/api/users/change-password', {
                currentPassword: formData.currentPassword,
                newPassword: formData.newPassword
            });
            setSuccess('Password changed successfully!');
            setFormData({...formData, currentPassword: '', newPassword: '', confirmPassword: ''});
        } catch (error) {
            console.error('Error changing password:', error);
            setError('Failed to change password. Please check your current password and try again.');
        }
    };

    if (!user) return <div className="loading">Loading...</div>;

    return (
        <div className="profile-container">
            <div className="profile-header">
                <img src={user.profilePicture} alt="Profile" className="profile-picture" />
                <div className="profile-info">
                    <h1>{user.displayName || user.username}</h1>
                    <p className="username">@{user.username}</p>
                    <p className="email">{user.email}</p>
                </div>
            </div>

            {error && <div className="error-message">{error}</div>}
            {success && <div className="success-message">{success}</div>}

            <div className="profile-tabs">
                <button 
                    className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
                    onClick={() => setActiveTab('profile')}
                >
                    Profile Information
                </button>
                <button 
                    className={`tab ${activeTab === 'password' ? 'active' : ''}`}
                    onClick={() => setActiveTab('password')}
                >
                    Change Password
                </button>
            </div>

            <div className="profile-content">
                {activeTab === 'profile' && (
                    <form onSubmit={handleSubmit} className="profile-form">
                        <div className="form-group">
                            <label htmlFor="displayName">Display Name</label>
                            <input
                                type="text"
                                id="displayName"
                                name="displayName"
                                value={formData.displayName || ''}
                                onChange={handleInputChange}
                            />
                        </div>
                        {/* Add similar form groups for fullName, dateOfBirth, location, bio, website */}
                        <button type="submit" className="submit-button">Save Changes</button>
                    </form>
                )}

                {activeTab === 'password' && (
                    <form onSubmit={handlePasswordChange} className="password-form">
                        <div className="form-group">
                            <label htmlFor="currentPassword">Current Password</label>
                            <input
                                type="password"
                                id="currentPassword"
                                name="currentPassword"
                                value={formData.currentPassword || ''}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        {/* Add similar form groups for newPassword and confirmPassword */}
                        <button type="submit" className="submit-button">Change Password</button>
                    </form>
                )}
            </div>
        </div>
    );
};

export default Profile;
