import React, { useState, useEffect } from 'react';
import './css/Post.css';
import { Heart, MessageCircle, Share2, MoreHorizontal, Send, Edit, Trash2 } from 'lucide-react';
import axios from '../axios';

export default function Post({ post, onPostUpdate, onPostDelete }) {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [editingPost, setEditingPost] = useState(false);
    const [editingComment, setEditingComment] = useState(null);
    const [editedContent, setEditedContent] = useState('');
    const defaultAvatar = "https://via.placeholder.com/40";

    useEffect(() => {
        fetchComments();
    }, [post._id]);

    const fetchComments = async () => {
        try {
            const response = await axios.get(`/api/posts/${post._id}/comments`);
            setComments(response.data);
        } catch (error) {
            console.error('Error fetching comments:', error);
        }
    };

    const handleAddComment = async () => {
        if (!newComment.trim()) return;

        try {
            const user = JSON.parse(localStorage.getItem('user'));
            const response = await axios.post(`/api/posts/${post._id}/comments`, {
                content: newComment,
                userId: user._id,
                username: user.username
            });
            setComments([...comments, response.data]);
            setNewComment('');
        } catch (error) {
            console.error('Error adding comment:', error);
        }
    };

    const handleEditPost = () => {
        setEditingPost(true);
        setEditedContent(post.content);
    };

    const handleUpdatePost = async () => {
        try {
            const response = await axios.put(`/api/posts/${post._id}`, {
                content: editedContent,
                userId: post.userId
            });
            onPostUpdate(response.data);
            setEditingPost(false);
        } catch (error) {
            console.error('Error updating post:', error);
        }
    };

    const handleDeletePost = async () => {
        try {
            await axios.delete(`/api/posts/${post._id}`);
            onPostDelete(post._id);
        } catch (error) {
            console.error('Error deleting post:', error);
        }
    };

    const handleEditComment = (comment) => {
        setEditingComment(comment._id);
        setEditedContent(comment.content);
    };

    const handleUpdateComment = async (commentId) => {
        try {
            const response = await axios.put(`/api/posts/${post._id}/comments/${commentId}`, {
                content: editedContent
            });
            setComments(comments.map(c => c._id === commentId ? response.data : c));
            setEditingComment(null);
        } catch (error) {
            console.error('Error updating comment:', error);
        }
    };

    const handleDeleteComment = async (commentId) => {
        try {
            await axios.delete(`/api/posts/${post._id}/comments/${commentId}`);
            setComments(comments.filter(c => c._id !== commentId));
        } catch (error) {
            console.error('Error deleting comment:', error);
        }
    };

    return (
        <div className="post">
            <div className="post-header">
                <img 
                    src={post.userProfilePicture || defaultAvatar} 
                    alt={post.user} 
                    className="avatar" 
                    onError={(e) => { e.target.onerror = null; e.target.src = defaultAvatar; }}
                />
                <div className="post-info">
                    <h3>{post.user}</h3>
                    <span>{new Date(post.createdAt).toLocaleString()}</span>
                </div>
                <div className="post-options">
                    <MoreHorizontal size={20} className="more-options" onClick={() => setEditingPost(!editingPost)} />
                    {editingPost && (
                        <div className="options-dropdown">
                            <button onClick={handleEditPost}><Edit size={16} /> Edit</button>
                            <button onClick={handleDeletePost}><Trash2 size={16} /> Delete</button>
                        </div>
                    )}
                </div>
            </div>
            <div className="post-content">
                {post.title && <h4>{post.title}</h4>}
                {editingPost ? (
                    <div>
                        <textarea
                            value={editedContent}
                            onChange={(e) => setEditedContent(e.target.value)}
                        />
                        <button onClick={handleUpdatePost}>Save</button>
                        <button onClick={() => setEditingPost(false)}>Cancel</button>
                    </div>
                ) : (
                    <p>{post.content}</p>
                )}
            </div>
            <div className="post-actions">
                <button><Heart size={20} /> {post.likes.length}</button>
                <button><MessageCircle size={20} /> {comments.length}</button>
                <button><Share2 size={20} /></button>
            </div>
            <div className="post-comments">
                {comments.map((comment) => (
                    <div key={comment._id} className="comment">
                        <img 
                            src={defaultAvatar} 
                            alt={comment.username} 
                            className="avatar-small" 
                        />
                        <div className="comment-content">
                            <strong>{comment.username}</strong>
                            {editingComment === comment._id ? (
                                <div>
                                    <textarea
                                        value={editedContent}
                                        onChange={(e) => setEditedContent(e.target.value)}
                                    />
                                    <button onClick={() => handleUpdateComment(comment._id)}>Save</button>
                                    <button onClick={() => setEditingComment(null)}>Cancel</button>
                                </div>
                            ) : (
                                <p>{comment.content}</p>
                            )}
                        </div>
                        <div className="comment-options">
                            <MoreHorizontal size={16} onClick={() => setEditingComment(comment._id)} />
                            {editingComment === comment._id && (
                                <div className="options-dropdown">
                                    <button onClick={() => handleEditComment(comment)}><Edit size={14} /> Edit</button>
                                    <button onClick={() => handleDeleteComment(comment._id)}><Trash2 size={14} /> Delete</button>
                                </div>
                            )}
                        </div>
                    </div>
                ))}
            </div>
            <div className="post-comment-input">
                <input
                    type="text"
                    placeholder="Write a comment..."
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                />
                <button onClick={handleAddComment}><Send size={20} /></button>
            </div>
        </div>
    );
}
