import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import './App.css';
import Feed from './components/Feed';
import Header from './components/Header';
import AuthForm from './components/AuthForm';
import Profile from './components/Profile';
import ThreadsPage from './components/ThreadsPage';
import ForumPage from './components/ForumPage';
import AdminDashboard from './components/AdminDashboard';
import { updateContext } from './context/updateContext';
import ThreadView from './components/ThreadView';

function App() {
    const [user, setUser] = useState(null);
    const [updater, setUpdater] = useState(0);

    useEffect(() => {
        const token = localStorage.getItem('token');
        const storedUser = localStorage.getItem('user');
        if (token && storedUser) {
            try {
                const parsedUser = JSON.parse(storedUser);
                setUser(parsedUser);
            } catch (error) {
                console.error('Error parsing stored user data:', error);
                localStorage.removeItem('user');
                localStorage.removeItem('token');
            }
        }
    }, []);

    const handleAuth = (userData) => {
        setUser(userData);
        localStorage.setItem('user', JSON.stringify(userData));
    };

    const handleLogout = () => {
        localStorage.removeItem('user');
        localStorage.removeItem('token');
        setUser(null);
    };

    return (
        <Router>
            <div className="App">
                <updateContext.Provider value={[updater, setUpdater]}>
                    <Header user={user} setUser={setUser} />
                    <Routes>
                        <Route path="/" element={user ? <Feed /> : <Navigate to="/login" />} />
                        <Route path="/login" element={<AuthForm setUser={setUser} />} />
                        <Route path="/register" element={<AuthForm setUser={setUser} isRegister />} />
                        <Route path="/profile" element={user ? <Profile user={user} setUser={setUser} /> : <Navigate to="/login" />} />
                        <Route path="/threads" element={<ThreadsPage />} />
                        <Route path="/forum" element={<ForumPage />} />
                        <Route path="/thread/:threadId" element={<ThreadView />} />
                        {/* Add the new admin route */}
                        <Route path="/admin" element={user && user.isAdmin ? <AdminDashboard /> : <Navigate to="/" />} />
                    </Routes>
                </updateContext.Provider>
            </div>
        </Router>
    );
}

export default App;
